INSERT INTO litcode.Question (id, title, description, code_content, hint, difficulty, likes, dislikes) VALUES (393, 'UTF-8 Validation', '<p>A character in UTF8 can be from <b>1 to 4 bytes</b> long, subjected to the following rules:</p>
