INSERT INTO litcode.Question (id, title, description, code_content, hint, difficulty, likes, dislikes) VALUES (741, 'Cherry Pickup', '<p>In a N x N <code>grid</code> representing a field of cherries, each cell is one of three possible integers.</p>

