INSERT INTO litcode.Question (id, title, description, code_content, hint, difficulty, likes, dislikes) VALUES (819, 'Minimum Swaps To Make Sequences Increasing', '<p>We have two integer sequences <code>A</code> and <code>B</code> of the same non-zero length.</p>

