INSERT INTO litcode.Question (id, title, description, code_content, hint, difficulty, likes, dislikes) VALUES (842, 'Card Flipping Game', '<p>On a table are <code>N</code> cards, with a positive integer printed on the front and back of each card (possibly different).</p>

