INSERT INTO litcode.Question (id, title, description, code_content, hint, difficulty, likes, dislikes) VALUES (871, 'Keys and Rooms', '<p>There are <code>N</code> rooms and you start in room <code>0</code>.&nbsp; Each room has a distinct number in <code>0, 1, 2, ..., N-1</code>, and each room may have&nbsp;some keys to access the next room.&nbsp;</p>

