INSERT INTO litcode.Question (id, title, description, code_content, hint, difficulty, likes, dislikes) VALUES (958, 'Sort Array By Parity II', '<p>Given an array <code>A</code>&nbsp;of non-negative integers, half of the integers in A are odd, and half of the integers are even.</p>

