INSERT INTO litcode.Question (id, title, description, code_content, hint, difficulty, likes, dislikes) VALUES (964, 'Minimize Malware Spread II', '<p>(This problem is the same as <em>Minimize Malware Spread</em>, with the differences bolded.)</p>

