INSERT INTO litcode.Question (id, title, description, code_content, hint, difficulty, likes, dislikes) VALUES (973, 'Stamping The Sequence', '<p>You want to form a <code>target</code>&nbsp;string of <strong>lowercase letters</strong>.</p>

