INSERT INTO litcode.Question (id, title, description, code_content, hint, difficulty, likes, dislikes) VALUES (993, 'Tallest Billboard', '<p>You are installing a billboard and want it to have the largest height.&nbsp; The billboard will have two steel supports, one on each side.&nbsp; Each steel support must be an equal height.</p>

