INSERT INTO litcode.Question (id, title, description, code_content, hint, difficulty, likes, dislikes) VALUES (994, 'Prison Cells After N Days', '<p>There are 8 prison cells in a row, and each cell is either occupied or vacant.</p>

<p>Each day, whether the cell is occupied or vacant changes according to the following rules:</p>

