INSERT INTO litcode.Question (id, title, description, code_content, hint, difficulty, likes, dislikes) VALUES (1027, 'Sum of Even Numbers After Queries', '<p>We have an array <code>A</code> of integers, and an array <code>queries</code>&nbsp;of queries.</p>

