INSERT INTO litcode.Question (id, title, description, code_content, hint, difficulty, likes, dislikes) VALUES (1035, 'Cousins in Binary Tree', '<p>In a binary tree, the root node is at depth <code>0</code>, and children of each depth <code>k</code> node are at depth <code>k+1</code>.</p>

